gr_group_members.query();
// add additional check here?
gs.info(gr_group_members.getRowCount());
gr_group_members.deleteMultiple(); 










function getEncodedQuery(opdiv, add_these_pocs_arr) {
    // gets encoded query that finds all records in the specified group and not in the add_these_pocs_arr
    var encoded_query = "group.name=VMO " + opdiv + " POCs";
    for (var i = 0; i < add_these_pocs_arr.length; i++) {
		encoded_query += "^user!=" + add_these_pocs_arr[i];
    }

    return encoded_query;
}
















